import React, { useState } from "react";
import "./LoginForm.css";
import { toast } from "react-toastify";
import { loginUser, validateEmail } from "../../services/authService";
import { SET_LOGIN, SET_NAME } from "../../redux/features/auth/authSlice";
import { useDispatch } from "react-redux";
import { Link, useNavigate } from "react-router-dom";

const initialState = {
  email: "",
  password: "",
};

const Login = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [formData, setformData] = useState(initialState);
  const { email, password } = formData;

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setformData({ ...formData, [name]: value });
  };

  const login = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      return toast.error("All fields are required");
    }

    if (!validateEmail(email)) {
      return toast.error("Please enter a valid email");
    }

    const userData = {
      email,
      password,
    };
    setIsLoading(true);
    try {
      const data = await loginUser(userData);
      // console.log(data);
      await dispatch(SET_LOGIN(true));
      await dispatch(SET_NAME(data.name));
      navigate("/dashboard");
      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
    }
  };
};
// onclick="document.getElementById('id01').style.display='block'" style="width:auto;"
function LoginForm() {
  //   const [name, setName] = useState(null);
  //   const [email, setEmail] = useState(null);
  //   const [password, setPassword] = useState(null);

  //   const [formData, setFormData] = useState({});
  //   const getFormData = async () => {
  //     const data = await axios.post("http://localhost:8080/user/sign-in");

  //   };

  //   useEffect(() => {

  //   }, []);

  return (
    <div>
      <form onSubmit={Login}>
        <section className="login">
          <div className="login_box">
            <div className="left">
              <div className="top_link">
                <Link to="/">
                  <img
                    src="https://drive.google.com/u/0/uc?id=16U__U5dJdaTfNGobB_OpwAJ73vM50rPV&export=download"
                    alt=""
                  />
                  Return home
                </Link>
              </div>

              <div className="contact">
                <form action="">
                  <h3>SIGN IN</h3>
                  <input type="text" placeholder="USERNAME" />
                  <input type="text" placeholder="PASSWORD" />
                  <button className="submit">LET'S GO</button>
                </form>
              </div>
            </div>
            <div className="right">
              <div className="right-text">
                <h2>The Tech Geek</h2>
                <h5>A Technical Blogging Website</h5>
              </div>
              <div className="right-inductor">
                <img src="" alt="" />
              </div>
            </div>
          </div>
        </section>
      </form>
    </div>
  );
}

export default LoginForm;
